places_supercat_mapping_tensor = places_supercat_mapping_tensor[swap,:]


def parse_args():
    parser = argparse.ArgumentParser()
    # Paths
    parser.add_argument('--src1', type=str, default='',
                        help='Path to the main checkpoint')
    parser.add_argument('--src2', type=str, default='',
                        help='Path to the secondary checkpoint (for combining)')
    parser.add_argument('--tar-name', type=str, default='',
                        help='Output tensor name')
    # task
    parser.add_argument('--task', choices=['add','addfixed','addfixedmax','modify','combine','compare'],
                        required=True,
                        help='Task to be performed. '
                             'add = add second classification layer '
                             'addfixed = add second classification layer with fixed binary weights '
                             'addfixedmax = add max + second classification layer with fixed binary weights '
                             'modify = change number of classes in classfication layer and reset weights to random '
                             'combine = combine classification layers of both sources'
                             'compare = compare weights per layer')
    parser.add_argument('--level',type=int,default=None,
                        help='Level of supercategories: 0 = 2, 1 = 3, 2 = 16, 3 = 365 (to create layer copying fine-grained classes)')

    parser.add_argument('--nclasses',type=int,default=None,
                        help='Set number of classes explicitly')

    args = parser.parse_args()
    return args



def perform_task(args):
    # Load checkpoints
    ckpt = torch.load(args.src1)
    save_name = args.tar_name 
    if args.task == 'combine' or args.task == 'compare':
        ckpt2 = torch.load(args.src2)
    else:
        ckpt2 = None

    reset_ckpt(ckpt)

    nclasses = 365
    if args.level is not None:
    
        if args.level==0:
            nclasses = 2
        elif args.level==1:
            nclasses = 3
        elif args.level==2:
            nclasses = 16
    elif args.nclasses is not None:
        nclasses = args.nclasses

    sdictname = 'model_state_dict'
    if 'model_state_dict' not in ckpt.keys():
        sdictname = 'state_dict'

    if args.task=='add' or args.task=='addfixed' or args.task=='addfixedmax':
        nw0 = 'classifier.0.weight'
        nb0 = 'classifier.0.bias'
        nw1 = 'classifier.1.weight'
        nb1 = 'classifier.1.bias'
        
        if args.task=='addfixedmax':
            nw0 = 'classifier.fc1.weight'
            nb0 = 'classifier.fc1.bias'
            nw1 = 'classifier.fc2.weight'
            nb1 = 'classifier.fc2.bias'        
        
    
        # rename existing classifier layer, add a new one (with random weights)
        ckpt[sdictname][nw0] = ckpt[sdictname]['classifier.weight']
        ckpt[sdictname][nb0] = ckpt[sdictname]['classifier.bias']
        
        del ckpt[sdictname]['classifier.weight']
        del ckpt[sdictname]['classifier.bias']
        
        ckpt[sdictname][nw1] = torch.rand(nclasses,365)
        ckpt[sdictname][nb1] = torch.zeros(nclasses)
        
        if args.task == 'addfixed' or args.task == 'addfixedmax':
            places_supercat_mapping_np = np.array(places_supercat_mapping_tensor)
            if args.task == 'addfixed':
                places_supercat_mapping_np = places_supercat_mapping_np / (places_supercat_mapping_np.sum(axis=1))[:,np.newaxis]
            if args.level == 3:
                ckpt[sdictname][nw1] = torch.eye(365)
            elif args.level == 2:
                ckpt[sdictname][nw1] = torch.tensor(places_supercat_mapping_np[4:])
            elif args.level == 1:
                ckpt[sdictname][nw1] = torch.tensor([places_supercat_mapping_np[0],places_supercat_mapping_np[2],places_supercat_mapping_np[3]]) 
            elif args.level == 0:
                ckpt[sdictname][nw1] = torch.tensor(places_supercat_mapping_np[:2]) 
        
    elif args.task=='modify':
        class_in_sz = ckpt[sdictname]['classifier.weight'].size()
        ckpt[sdictname]['classifier.weight'] = torch.rand(nclasses,class_in_sz[1])
        ckpt[sdictname]['classifier.bias'] = torch.zeros(nclasses)
    elif args.task=='combine':
        print('tbd')
    elif args.task=='compare':
        for k in ckpt[sdictname].keys():
            if k in ckpt2[sdictname].keys():
                if torch.equal(ckpt[sdictname][k],ckpt2[sdictname][k]):
                    print(k+': equal')
                else:
                    print(k+': different')
            else:
                print(k+': not found')
    else:
        print('undefined task: '+str(args.task))

    # Save to file
    if not(args.task == 'compare'):
        save_ckpt(ckpt, save_name)


def save_ckpt(ckpt, save_name):
    torch.save(ckpt, save_name)
    print('save changed ckpt to {}'.format(save_name))


def reset_ckpt(ckpt):
    if 'scheduler' in ckpt:
        del ckpt['scheduler']
    if 'optimizer' in ckpt:
        del ckpt['optimizer']
    if 'iteration' in ckpt:
        ckpt['iteration'] = 0



def main(arglist):

    sys.argv = arglist
    sys.argc = len(arglist)

    args = parse_args()


    perform_task(args)
        
        
if __name__ == '__main__':
    
    main(sys.argv)
    
